<?phpclass DataModel{    /**     * @param $table     * @param array $row     * @param array $content     * @return bool     */    public static function add($table, array $row, array $content)    {        return Db::getInstance()->insert('INSERT', $table, $row, $content);    }    public static function edit($table, array $fields, array $content, $id)    {        $where = 'id=' . (int)$id;        return Db::getInstance()->insert('UPDATE', $table, $fields, $content, $where);    }}